document.addEventListener("DOMContentLoaded",(()=>{console.log("Hack Club Events script loaded");const e=document.getElementById("hack-club-events");fetch("https://api.codetabs.com/v1/proxy?quest=https://events.hackclub.com/api/events/upcoming").then((e=>e.text())).then((n=>{let t;console.log("Raw response:",n);try{t=JSON.parse(n)}catch(e){throw console.error("JSON parse error:",n),e}e.innerHTML=`\n        <h2>Upcoming Hack Club Events</h2>\n        <div class="events-list">\n          ${0===t.length?"<p>No upcoming events found.</p>":""}\n          ${t.map((e=>`\n            <div class="event">\n              <div class="event-title">${e.title}</div>\n              <div class="event-time">${new Date(e.start).toLocaleString()}</div>\n              <div class="event-desc">${e.desc}</div>\n            </div>\n          `)).join("")}\n          <br>\n      `})).catch((n=>{console.error("Fetch error:",n),e.innerHTML=`<p>Error fetching events: ${n.message}</p>`}))}));